const handleFilterClick = (status) => {
  if (activeFilter === status) {
    // If clicking the same filter again, clear the filter
    setActiveFilter(null);
  } else {
    setActiveFilter(status);
  }
};

// Get counts by status
const getStatusCounts = () => {
  const counts = {
    green: 0,
    yellow: 0,
    red: 0
  };

  historyData.forEach(item => {
    counts[item.status] = (counts[item.status] || 0) + 1;
  });

  return counts;
};import React, { useState, useEffect } from 'react';
import { historyApi } from '../services/api';
import StatusIndicator from '../components/StatusIndicator';
import './GlobalHistoryView.css';

const GlobalHistoryView = () => {
const [historyData, setHistoryData] = useState([]);
const [filteredData, setFilteredData] = useState([]);
const [activeFilter, setActiveFilter] = useState(null);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
const [viewMode, setViewMode] = useState('country');  // 'country' or 'phone'
const [searchTerm, setSearchTerm] = useState('');


// Filter settings
const [timeRange, setTimeRange] = useState('5d');
const [startDate, setStartDate] = useState('');
const [endDate, setEndDate] = useState('');
const [showCustomDates, setShowCustomDates] = useState(false);

useEffect(() => {
  fetchHistoryData();
}, [timeRange]);

// Apply filters when activeFilter changes or historyData updates
useEffect(() => {
  if (!activeFilter) {
    setFilteredData(historyData);
    return;
  }

  const filtered = historyData.filter(item => item.status === activeFilter);
  setFilteredData(filtered);
}, [activeFilter, historyData]);

const fetchHistoryData = async () => {
  try {
    setLoading(true);

    let params = { timeRange };
    if (timeRange === 'custom' && startDate && endDate) {
      params.startDate = startDate;
      params.endDate = endDate;
    }

    const response = await historyApi.getStatusHistory(
      params.timeRange,
      params.startDate,
      params.endDate
    );

    setHistoryData(response.data);
    setFilteredData(response.data);
    setLoading(false);
  } catch (err) {
    setError('Failed to fetch history data. Please try again later.');
    setLoading(false);
    console.error('Error fetching history data:', err);
  }
};

// Format date to readable format
const formatDate = (dateString) => {
  if (!dateString) return 'Present';
  const date = new Date(dateString);
  return date.toLocaleString();
};

// Calculate duration between start and end times
const calculateDuration = (startDate, endDate) => {
  if (!endDate) return 'Ongoing';

  const start = new Date(startDate);
  const end = new Date(endDate);
  const durationMs = end - start;

  // Less than a minute
  if (durationMs < 60000) {
    return `${Math.floor(durationMs / 1000)} seconds`;
  }
  // Less than an hour
  if (durationMs < 3600000) {
    return `${Math.floor(durationMs / 60000)} minutes`;
  }
  // Less than a day
  if (durationMs < 86400000) {
    return `${Math.floor(durationMs / 3600000)} hours`;
  }
  // More than a day
  return `${Math.floor(durationMs / 86400000)} days`;
};

const handleTimeRangeChange = (e) => {
  const newRange = e.target.value;
  setTimeRange(newRange);
  setShowCustomDates(newRange === 'custom');
};

const handleCustomDateSubmit = (e) => {
  e.preventDefault();
  fetchHistoryData();
};

// Group history data by country or phone number
const groupData = (data = filteredData) => {
  const grouped = {};

  if (viewMode === 'country') {
    data.forEach(item => {
      if (!grouped[item.country]) {
        grouped[item.country] = [];
      }
      grouped[item.country].push(item);
    });
  } else {
    data.forEach(item => {
      if (!grouped[item.number]) {
        grouped[item.number] = [];
      }
      grouped[item.number].push(item);
    });
  }

  return grouped;
};

if (loading) {
  return <div className="loading">Loading history data...</div>;
}

if (error) {
  return <div className="error">{error}</div>;
}

const groupedData = groupByCountry();

return (
  <div className="history-view">
    <h1>Global Status History</h1>

    <div className="filter-controls">
      <div className="time-range-selector">
        <label htmlFor="timeRange">Time Range:</label>
        <select 
          id="timeRange" 
          value={timeRange} 
          onChange={handleTimeRangeChange}
          className="time-range-select"
        >
          <option value="24h">Last 24 Hours</option>
          <option value="48h">Last 48 Hours</option>
          <option value="5d">Last 5 Days</option>
          <option value="custom">Custom Range</option>
        </select>
      </div>

      {showCustomDates && (
        <form onSubmit={handleCustomDateSubmit} className="custom-date-form">
          <div className="date-input-group">
            <label htmlFor="startDate">Start Date:</label>
            <input 
              type="datetime-local" 
              id="startDate" 
              value={startDate} 
              onChange={(e) => setStartDate(e.target.value)}
              required
            />
          </div>

          <div className="date-input-group">
            <label htmlFor="endDate">End Date:</label>
            <input 
              type="datetime-local" 
              id="endDate" 
              value={endDate} 
              onChange={(e) => setEndDate(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="apply-btn">Apply</button>
        </form>
      )}
    </div>

    <div className="history-summary">
      <div className="summary-cards">
        <div 
          className={`summary-card ${activeFilter === 'green' ? 'active' : ''}`}
          onClick={() => handleFilterClick('green')}
        >
          <h3>Online Events</h3>
          <div className="summary-count green">{getStatusCounts().green}</div>
          {activeFilter === 'green' && <div className="filter-badge">Filtered</div>}
        </div>

        <div 
          className={`summary-card ${activeFilter === 'yellow' ? 'active' : ''}`}
          onClick={() => handleFilterClick('yellow')}
        >
          <h3>Degraded Events</h3>
          <div className="summary-count yellow">{getStatusCounts().yellow || 0}</div>
          {activeFilter === 'yellow' && <div className="filter-badge">Filtered</div>}
        </div>

        <div 
          className={`summary-card ${activeFilter === 'red' ? 'active' : ''}`}
          onClick={() => handleFilterClick('red')}
        >
          <h3>Offline Events</h3>
          <div className="summary-count red">{getStatusCounts().red}</div>
          {activeFilter === 'red' && <div className="filter-badge">Filtered</div>}
        </div>
      </div>

      <div className="summary-info">
        <div className="info-card">
          <h3>Events</h3>
          <div className="info-value">{historyData.length}</div>
          <div className="info-detail">Total status changes</div>
        </div>
        <div className="info-card">
          <h3>Countries</h3>
          <div className="info-value">{Object.keys(groupByCountry(filteredData)).length}</div>
          <div className="info-detail">
            {activeFilter ? `With ${activeFilter === 'green' ? 'online' : activeFilter === 'yellow' ? 'degraded' : 'offline'} events` : 'With status changes'}
          </div>
        </div>
      </div>

      {activeFilter && (
        <button 
          className="clear-filter"
          onClick={() => setActiveFilter(null)}
        >
          Clear Filter
        </button>
      )}
    </div>

    <div className="history-list">
      {Object.entries(groupedData).map(([country, events]) => (
        <div key={country} className="country-history-card">
          <h2>{country}</h2>
          <table className="history-table">
            <thead>
              <tr>
                <th>Status</th>
                <th>Started</th>
                <th>Ended</th>
                <th>Duration</th>
              </tr>
            </thead>
            <tbody>
              {events.map((event) => (
                <tr key={event.id} className={`status-${event.status}`}>
                  <td>
                    <StatusIndicator status={event.status} />
                  </td>
                  <td>{formatDate(event.status_start)}</td>
                  <td>{formatDate(event.status_end)}</td>
                  <td>{calculateDuration(event.status_start, event.status_end)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  </div>
);
};

export default GlobalHistoryView;
