import React, { useState, useEffect } from 'react';
import { historyApi } from '../services/api';
import StatusIndicator from '../components/StatusIndicator';
import './GlobalHistoryView.css';

const GlobalHistoryView = () => {
  const [historyData, setHistoryData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filter settings
  const [timeRange, setTimeRange] = useState('5d');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [showCustomDates, setShowCustomDates] = useState(false);

  useEffect(() => {
    fetchHistoryData();
  }, [timeRange]);

  const fetchHistoryData = async () => {
    try {
      setLoading(true);

      let params = { timeRange };
      if (timeRange === 'custom' && startDate && endDate) {
        params.startDate = startDate;
        params.endDate = endDate;
      }

      const response = await historyApi.getStatusHistory(
        params.timeRange,
        params.startDate,
        params.endDate
      );

      setHistoryData(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch history data. Please try again later.');
      setLoading(false);
      console.error('Error fetching history data:', err);
    }
  };

  // Format date to readable format
  const formatDate = (dateString) => {
    if (!dateString) return 'Present';
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  // Calculate duration between start and end times
  const calculateDuration = (startDate, endDate) => {
    if (!endDate) return 'Ongoing';

    const start = new Date(startDate);
    const end = new Date(endDate);
    const durationMs = end - start;

    // Less than a minute
    if (durationMs < 60000) {
      return `${Math.floor(durationMs / 1000)} seconds`;
    }
    // Less than an hour
    if (durationMs < 3600000) {
      return `${Math.floor(durationMs / 60000)} minutes`;
    }
    // Less than a day
    if (durationMs < 86400000) {
      return `${Math.floor(durationMs / 3600000)} hours`;
    }
    // More than a day
    return `${Math.floor(durationMs / 86400000)} days`;
  };

  const handleTimeRangeChange = (e) => {
    const newRange = e.target.value;
    setTimeRange(newRange);
    setShowCustomDates(newRange === 'custom');
  };

  const handleCustomDateSubmit = (e) => {
    e.preventDefault();
    fetchHistoryData();
  };

  // Group history data by country
  const groupByCountry = () => {
    const grouped = {};

    historyData.forEach(item => {
      if (!grouped[item.country]) {
        grouped[item.country] = [];
      }
      grouped[item.country].push(item);
    });

    return grouped;
  };

  if (loading) {
    return <div className="loading">Loading history data...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  const groupedData = groupByCountry();

  return (
    <div className="history-view">
      <h1>Global Status History</h1>

      <div className="filter-controls">
        <div className="time-range-selector">
          <label htmlFor="timeRange">Time Range:</label>
          <select 
            id="timeRange" 
            value={timeRange} 
            onChange={handleTimeRangeChange}
            className="time-range-select"
          >
            <option value="24h">Last 24 Hours</option>
            <option value="48h">Last 48 Hours</option>
            <option value="5d">Last 5 Days</option>
            <option value="custom">Custom Range</option>
          </select>
        </div>

        {showCustomDates && (
          <form onSubmit={handleCustomDateSubmit} className="custom-date-form">
            <div className="date-input-group">
              <label htmlFor="startDate">Start Date:</label>
              <input 
                type="datetime-local" 
                id="startDate" 
                value={startDate} 
                onChange={(e) => setStartDate(e.target.value)}
                required
              />
            </div>

            <div className="date-input-group">
              <label htmlFor="endDate">End Date:</label>
              <input 
                type="datetime-local" 
                id="endDate" 
                value={endDate} 
                onChange={(e) => setEndDate(e.target.value)}
                required
              />
            </div>

            <button type="submit" className="apply-btn">Apply</button>
          </form>
        )}
      </div>

      <div className="history-summary">
        <div className="summary-card">
          <h3>Total Status Changes</h3>
          <div className="summary-count">{historyData.length}</div>
        </div>
        <div className="summary-card">
          <h3>Countries Affected</h3>
          <div className="summary-count">{Object.keys(groupedData).length}</div>
        </div>
      </div>

      <div className="history-list">
        {Object.entries(groupedData).map(([country, events]) => (
          <div key={country} className="country-history-card">
            <h2>{country}</h2>
            <table className="history-table">
              <thead>
                <tr>
                  <th>Status</th>
                  <th>Started</th>
                  <th>Ended</th>
                  <th>Duration</th>
                </tr>
              </thead>
              <tbody>
                {events.map((event) => (
                  <tr key={event.id} className={`status-${event.status}`}>
                    <td>
                      <StatusIndicator status={event.status} />
                    </td>
                    <td>{formatDate(event.status_start)}</td>
                    <td>{formatDate(event.status_end)}</td>
                    <td>{calculateDuration(event.status_start, event.status_end)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GlobalHistoryView;
